//x轴边界
        if (x > ParCli.w * Xn) {
            x = ParCli.w * Xn
        } else if (x < -ParCli.w * Xn) {
            x = -ParCli.w * Xn
        }
//y轴边界
        if (y > ParCli.h * Yn) {
            y = ParCli.h * Yn
        } else if (y < -ParCli.h * Yn) {
            y = -ParCli.h * Yn
        }

